        if (BasketItem.Quantity <= 1)
        {
            // var basket = basketService.GetBasket(BasketItem.BasketId);
            // basket.TotalPrice -= BasketItem.Price;
            var deleted = basketService.DeleteProduct(BasketItem.BasketId, BasketItem.ProductId);
